import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Introduction = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { opacity: 1, y: 0 },
  };

  return (
    <motion.div
      ref={ref}
      initial="hidden"
      animate={inView ? 'visible' : 'hidden'}
      variants={containerVariants}
      transition={{ duration: 0.8, ease: 'easeOut' }}
      className="w-full h-full flex flex-col justify-center items-center py-16"
    >
      <motion.h1 
        className="text-6xl md:text-7xl font-heading font-bold bg-gradient-to-r from-blue-600 to-purple-600 text-transparent bg-clip-text mb-8"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
      >
        Hello! I'm <span className="text-primary">Hazel</span>
      </motion.h1>
      
      <motion.p 
        className="text-xl md:text-2xl text-gray-600 mb-16 max-w-2xl"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        I'm passionate about programming systems and creating elegant solutions.
      </motion.p>
      
      <div className="flex flex-col md:flex-row gap-6 justify-center">
        <motion.a
          href="https://drive.google.com/file/d/1TiZCMu1PcBgS3FjKGjE4Yj_gpou1qnWJ/view?usp=sharing"
          target="_blank"
          rel="noopener noreferrer"
          className="inline-flex items-center justify-center px-8 py-3 text-lg font-medium text-white bg-blue-600 hover:bg-blue-700 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          View my CV
        </motion.a>
        
        <motion.a
          href="https://drive.google.com/drive/folders/10pKVtrHiR2vzPq8KNGMeSg-g-acXyMIh?usp=sharing"
          target="_blank"
          rel="noopener noreferrer"
          className="inline-flex items-center justify-center px-8 py-3 text-lg font-medium border-2 border-blue-600 text-blue-600 hover:bg-blue-50 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300"
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
        >
          View Projects
        </motion.a>
      </div>
    </motion.div>
  );
};

export default Introduction;
