import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const ProjectCard = ({ title, type, description, image, link }) => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  return (
    <motion.div
      ref={ref}
      initial={{ opacity: 0, y: 20 }}
      animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
      transition={{ duration: 0.6 }}
      className="w-full md:w-1/2 lg:w-1/3 p-4"
    >
      <motion.div
        whileHover={{ y: -5 }}
        className="bg-white rounded-lg shadow-lg overflow-hidden h-full"
      >
        <div 
          className="h-48 bg-cover bg-center" 
          style={{ backgroundImage: `url(${image})` }}
        />
        <div className="p-6">
          <h3 className="text-xl font-bold mb-2 text-text">{title}</h3>
          <p className="text-sm font-medium text-primary mb-4">{type}</p>
          <p className="text-text/70 mb-6 text-sm">{description}</p>
          {link && (
            <motion.a
              href={link}
              target="_blank"
              rel="noopener noreferrer"
              className="inline-flex items-center text-primary hover:text-primary/80 transition-colors"
              whileHover={{ x: 5 }}
            >
              Learn More
              <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
              </svg>
            </motion.a>
          )}
        </div>
      </motion.div>
    </motion.div>
  );
};

const Projects = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const projects = [
    {
      title: 'Kolehiyuh: School Finder',
      type: 'Mobile Application',
      description: 'An application that helps high school graduates choose their college schools by providing a centralized platform for browsing school admissions and entrance exam applications.',
      image: 'images/proj-1.jpg',
      link: 'https://drive.google.com/file/d/1zVcA3jGpiYqrd66fgALwGcGHKU5rOesL/view?usp=sharing'
    },
    {
      title: 'Hidden Rule',
      type: 'Game',
      description: 'A card game where players must deduce hidden rules. Played with a dealer and four players, each having 13 cards while the dealer manages 50 rule cards that determine valid plays.',
      image: 'images/proj-2.jpg',
      link: 'https://drive.google.com/file/d/1-d4Txk8KSYNdZKG7pnJcP8gVe08pNNZG/view?usp=sharing'
    }
  ];

  return (
    <section id="projects" className="py-20 px-4 bg-background">
      <div className="max-w-6xl mx-auto">
        <motion.div
          ref={ref}
          initial={{ opacity: 0, y: 20 }}
          animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl md:text-4xl font-bold font-heading mb-4 text-text">
            Recent Projects
          </h2>
          <p className="text-text/60 max-w-2xl mx-auto">
            Here are some of my recent works that showcase my skills and passion for development.
          </p>
        </motion.div>

        <div className="flex flex-wrap -m-4">
          {projects.map((project, index) => (
            <ProjectCard key={index} {...project} />
          ))}
        </div>

        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4 }}
          className="text-center mt-12"
        >
          <p className="text-text/60 text-lg font-medium">More projects coming soon!</p>
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
