import React from 'react';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

const Introduction = () => {
  const [ref, inView] = useInView({
    triggerOnce: true,
    threshold: 0.1,
  });

  const containerVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: { opacity: 1, y: 0 },
  };

  return (
    <section className="min-h-screen bg-gradient-to-br from-background to-secondary/10 flex items-center justify-center py-20 px-4">
      <motion.div
        ref={ref}
        initial="hidden"
        animate={inView ? 'visible' : 'hidden'}
        variants={containerVariants}
        transition={{ duration: 0.8, ease: 'easeOut' }}
        className="max-w-4xl mx-auto text-center"
      >
        <motion.h1 
          className="text-6xl md:text-7xl font-heading font-bold text-text mb-6"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2 }}
        >
          Hello! I'm <span className="text-primary">Hazel</span>
        </motion.h1>
        
        <motion.p 
          className="text-xl md:text-2xl text-text/80 mb-12"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.4 }}
        >
          I'm passionate about programming systems and creating elegant solutions.
        </motion.p>
        
        <div className="flex flex-col md:flex-row gap-4 justify-center">
          <motion.a
            href="https://drive.google.com/file/d/1TiZCMu1PcBgS3FjKGjE4Yj_gpou1qnWJ/view?usp=sharing"
            target="_blank"
            rel="noopener noreferrer"
            className="inline-flex items-center justify-center px-8 py-3 text-lg font-medium text-white bg-primary rounded-lg hover:bg-primary/90 transition-colors duration-300"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            View my CV
          </motion.a>
          
          <motion.a
            href="https://drive.google.com/drive/folders/10pKVtrHiR2vzPq8KNGMeSg-g-acXyMIh?usp=sharing"
            target="_blank"
            rel="noopener noreferrer"
            className="inline-flex items-center justify-center px-8 py-3 text-lg font-medium text-primary border-2 border-primary rounded-lg hover:bg-primary/10 transition-colors duration-300"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            View Projects
          </motion.a>
        </div>
      </motion.div>
    </section>
  );
};

export default Introduction;
